    __login: function(){
      if(this.refs.usr != "" && this.refs.pwd != ""){
        var nextPath = this.getQuery().nextPath;
        let self = this;
        UserActions.login({
          username: this.refs.usr.getDOMNode().value.trim(),
          password: this.refs.pwd.getDOMNode().value.trim(),
