                              </span>

                              <ul className="dropdown-menu dropdown-menu-right" role="menu">
                                {possibledropdown}
                              </ul>
                            </div>
                        );

                return (
                <span>
                    <div className="form-group">
                        <div className="input-group clearfix">
                            <span className="input-group-addon" id="state-title">
                                <strong>State Name</strong>
                            </span>
                            <input type="title" className="form-control"
                                            aria-describedby="state-title"
                                            placeholder="Enter the state name"
                                            onChange={this.setTitle} value={this.state.name} />
                        </div>
                    </div>
                    <ChildComponent main={this} />
                    <div className="form-group">
                        <div className="input-group">
                            <span className="input-group-addon" id="state-dependencies">
                                <strong>Dependencies</strong>
                            </span>
                            <div className="form-control" aria-describedby="study-title">
                             {dependencies.length === 0?
                                "There's no possible dependencies for this task.": dependencies}
                            </div>
                        </div>
                    </div>
                    <div className="clearfix">
                    <button onClick={this.save} className="pull-right btn btn-primary">Save Details</button>
                    </div>
                </span>
                );
            }
        });

        return DetailState;
    }

    detailProcess(data){
        return false;
    }

    static typeIcon(){
        return <i></i>;
    }
    static repr(){
        return 'Task';
    }

    static deserializeOptions(data){
        if(data.name === undefined)
            throw `data object must have at least a name property to be possible to deserialize it,
                    if it does not, a custom deserialize override method should be implemented in a
                    class that inherits from State or SimpleState`;

        return {name: data.name};
    }

    serialize(){
        let deps = [];
        for(let dep of this.getDependencies()){
            deps.push({
                id: dep.getIdentificator()
            });
        }

        return {
            id: this.__identificator,
            name: this.getData().name,
            dependencies: deps
        }
    }
}

